import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

const signs = ['+','-','x','/'];

const RainOfSigns = () => {
    const [signsList, setSignsList] = useState([]);

    useEffect(() => {
      const interval = setInterval(() => {
        const newSign = signs[Math.floor(Math.random() * signs.length)];
        const leftPosition = Math.random() * 100; // Posición horizontal aleatoria (0-100%)
        setSignsList((prev) => [...prev, { sign: newSign, left: leftPosition }]);
      }, 200); // Cada 200ms agrega un nuevo signo

      return () => clearInterval(interval); // Limpia el intervalo al desmontar
    }, []);

    return (
      <div className="rain-container">
        {signsList.map((item, index) => (
          <div
            key={index}
            className="falling-sign"
            style={{
              left: `${item.left}%`,
              animationDuration: `${Math.random() * 2 + 2}s`, // Duración aleatoria de la animación (2-4s)
              animationDelay: `${Math.random() * 2}s`, // Retraso aleatorio
            }}
          >
            {item.sign}
          </div>
        ))}
      </div>
    );
  };

  function Menu() {
    const navigate = useNavigate();

    const handlePlayClick = () => {
      navigate('/game'); // Redirige a la ruta "/game"
    };

    return (
      <div className="menu-container">
        <RainOfSigns /> {/* Llama al componente de lluvia de signos */}
        <h1 className="game-title">IQ180</h1>
        <button className="play-button" onClick={handlePlayClick}>
          Jugar
        </button>
      </div>
    );
  }

export default Menu;
