import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

const RandomLetter = () => {
  const [letter, setLetter] = useState('');

  useEffect(() => {
    const generateRandomLetter = () => {
      const letters = '+-/x';
      const randomIndex = Math.floor(Math.random() * letters.length);
      setLetter(letters[randomIndex]);
    };

    // Generar una letra al inicio
    generateRandomLetter();

    // Establecer un intervalo para generar nuevas letras cada segundo
    const interval = setInterval(generateRandomLetter, 1000);

    // Limpiar el intervalo al desmontar el componente
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ fontSize: '48px', textAlign: 'center', marginTop: '20%' }}>
      {letter}
    </div>
  );
};


function Menu() {
    RandomLetter;
  const navigate = useNavigate();

  // Función que se ejecuta al hacer clic en "Jugar"
  const handlePlayClick = () => {
    navigate('/game'); // Redirige a la ruta "/game"
  };

  return (
    <div className="menu-container">
      <h1 className="game-title">IQ180</h1>
      <button className="play-button" onClick={handlePlayClick}>
        Play
      </button>
    </div>
  );
}

export default Menu;
