import React, { useEffect, useState } from 'react';
import './App.css';

const RandomLetter = () => {
  const [letters, setLetters] = useState([]);

  const generateRandomLetter = () => {
    const lettersArray = '+-/x';
    const randomIndex = Math.floor(Math.random() * lettersArray.length);
    const newLetter = {
      char: lettersArray[randomIndex],
      left: Math.random() * window.innerWidth, // Posici贸n horizontal aleatoria
      top: 0, // Empieza desde la parte superior
    };
    setLetters((prevLetters) => [...prevLetters, newLetter]);
  };

  useEffect(() => {
    // Generar una letra al inicio
    const interval = setInterval(() => {
      generateRandomLetter();
    }, 300); // Cambiar letras cada 300 ms

    return () => clearInterval(interval);
  }, []);

  // Animar las letras
  useEffect(() => {
    const fallInterval = setInterval(() => {
      setLetters((prevLetters) => 
        prevLetters.map(letter => ({
          ...letter,
          top: letter.top + 5, // Caer 5px cada intervalo
        })).filter(letter => letter.top < window.innerHeight) // Eliminar letras que salieron de la pantalla
      );
    }, 50); // Actualizar la posici贸n cada 50 ms

    return () => clearInterval(fallInterval);
  }, []);

  return (
    <div className="rain-container">
      {letters.map((letter, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            left: letter.left,
            top: letter.top,
            fontSize: '24px',
            color: 'black', // Cambia el color si deseas
          }}
        >
          {letter.char}
        </div>
      ))}
    </div>
  );
};

export default RandomLetter;
